import {takeEvery,takeLatest,put, call} from "redux-saga/effects";

import {SONGS,ERRORS,REQUEST_API} from "../redux/types";


function* fetchSongs(action){
   try {
       const data = yield call(api.fetchData,action.payload);
       console.log(data)
       yield put({type:SONGS,payload:data});
   }catch(e){
       yield put({type:ERRORS,payload:e});
   }
}

export default function* watchApiRequests(){
    yield takeLatest(REQUEST_API,fetchSongs)
}
