import {createReducer} from "reduxsauce";
import Types from "../types";

const initialState = {
  songs: null,
  error: "",
};

export default function (state = initialState, action) {
  switch (action.type) {
    case SONGS:
      return {
        songs:action.payload
      };
    case ERRORS:
      return {
        error: action.payload,
      };
    default:
      return state;
  }
}
