import { SONGS, ERRORS , TOP_CHARTS } from "../types";

const initialState = {
  songs: null,
  error: "",
  text:"Top Drake Songs"
};

export default function (state = initialState, action) {
  switch (action.type) {
    case SONGS:
      return {
        songs:action.payload
      };
    case ERRORS:
      return {
        error: action.payload,
      };
    case TOP_CHARTS:
      return {
        text: ""
      }  
    default:
      return state;
  }
}
