import React from "react";
import { Input } from 'antd';
import {useSelector,useDispatch} from "react-redux";
import Card from "./Card";
import {fetchSongs} from "./redux/actions/songsAction";
const { Search } = Input;
function App(props) {
  const dispatch  = useDispatch();
  const onSearch = value=> {
    let url = `/search?term=${value}`
    dispatch(fetchSongs(url));
  }  
  const search = useSelector((state) => state.search);

  return (
    <>
    <div><img style={{marginLeft:"auto",marginRight:"auto",marginTop:"30px",display:"block"}} src="itunes.svg"></img></div>
    <h1 style={{textAlign:"center",marginTop:"20px",fontFamily:"apple"}}>Search latest songs from itunes</h1>
    <div style={{width:"100%",textAlign:"center",marginTop:"40px"}}>
     <Search placeholder="Search Songs" style={{width:"350px"}} onSearch={onSearch} enterButton />
    </div>
    <div className="ui segment" style={{width:"70%",margin:"auto",marginTop:"30px"}}>
    {
      search.songs?(search.songs.data.results.map(function(song){
        return <Card song={song} key={song.trackId}/>
       })):(<p>loading</p>)
    }
    </div>
    </>
  );
}

export default App;
