import {takeEvery,takeLatest,put, call} from "redux-saga/effects";

import {SONGS,ERRORS,REQUEST_API} from "../types";
import fetchData from "./api";

function* fetchSongs(url){
   try {
       const data = yield call(fetchData(url));
       yield put({type:SONGS,payload:data});
   }catch(e){
       yield put({type:ERRORS,payload:e});
   }
}

export default function* watchApiRequests(){
    yield takeEvery(REQUEST_API,fetchSongs)
}
